/* ESTUCTURA BD */
CREATE TABLE POKEDEX(
	NUM_POKEDEX INT PRIMARY KEY,
	NOM_POKEMON VARCHAR(100) NOT NULL,
	NIVEL_EVO INT,
	TIPO1 ENUM('NORMAL','FUEGO','AGUA','PLANTA','ELECTRICO','HIELO','LUCHA','VENENO','TIERRA','VOLADOR','PSIQUICO','BICHO','ROCA','FANTASMA','DRAGON') NOT NULL,
	TIPO2 ENUM('NORMAL','FUEGO','AGUA','PLANTA','ELECTRICO','HIELO','LUCHA','VENENO','TIERRA','VOLADOR','PSIQUICO','BICHO','ROCA','FANTASMA','DRAGON'),
	VITALIDAD INT NOT NULL,
	ATAQUE INT NOT NULL,
	AT_ESPECIAL INT NOT NULL,
	DEFENSA INT NOT NULL,
	DEF_ESPECIAL INT NOT NULL,
	VELOCIDAD INT NOT NULL
);

CREATE TABLE POKEMON(
	ID_POKEMON INT PRIMARY KEY AUTO_INCREMENT,
	ID_ENTRENADOR INT,
	ID_RIVAL INT,
	NUM_POKEDEX INT,
	MOTE VARCHAR(20),
	VITALIDAD_MAX INT NOT NULL,
	VITALIDAD_ACT INT NOT NULL,
	ATAQUE INT NOT NULL,
	AT_ESPECIAL INT NOT NULL,
	DEFENSA INT NOT NULL,
	DEF_ESPECIAL INT NOT NULL,
	VELOCIDAD INT NOT NULL,
	NIVEL INT NOT NULL,
	FERTILIDAD INT DEFAULT 5,
	SEXO ENUM('H','M','X'),
	ESTADO ENUM('PARALIZADO','QUEMADO','ENVENENADO','GRAVEMENTE ENVENENADO','DORMIDO','CONGELADO','HELADO','SOMNOLIENTO','POKERUS','DEBILITADO','CONFUSO','ENAMORADO','ATRAPADO','MALDITO','DRENADORAS','CANTO MORTAL','CENTRO DE ATENCION','AMEDRENTADO', 'SIN_ESTADO'),
	EQUIPO INT,
	TIPO_PROPIETARIO ENUM('ENTRENADOR', 'RIVAL'),
	EXPERIENCIA INT
);

CREATE TABLE MOVIMIENTO(
	ID_MOVIMIENTO INT NOT NULL PRIMARY KEY,
	NOM_MOVIMIENTO VARCHAR(20) NOT NULL,
	NIVEL_APRENDIZAJE INT NOT NULL,
	PP_MAX INT,
	TIPO VARCHAR(20),
	POTENCIA INT,
	TIPO_MOV ENUM('NORMAL','FUEGO','AGUA','PLANTA','ELECTRICO','HIELO','LUCHA','VENENO','TIERRA','VOLADOR','PSIQUICO','BICHO','ROCA','FANTASMA','DRAGON'),
	ESTADO VARCHAR(20),
	TURNOS INT,
	MEJORA VARCHAR(20),
	PROBABILIDAD INT
);

CREATE TABLE MOVIMIENTO_POKEMON(
    ID_POKEMON INT,
    ID_MOVIMIENTO INT,
    PP_ACTUALES INT
);

CREATE TABLE ENTRENADOR(
	ID_ENTRENADOR INT PRIMARY KEY,
	USUARIO VARCHAR(100) NOT NULL UNIQUE,
	PASS VARCHAR(20) NOT NULL,
	POKEDOLARES INT DEFAULT 1000,
	RIVALES_VENCIDOS INT
);

CREATE TABLE RIVAL(
	ID_RIVAL INT PRIMARY KEY,
	NOMBRE VARCHAR(100) NOT NULL UNIQUE,
	FRASE_VICTORIA VARCHAR(200),
	FRASE_DERROTA VARCHAR(200),
	FRASE_RENDICION VARCHAR(200)
);

CREATE TABLE OBJETO(
	ID_OBJETO INT PRIMARY KEY,
	NOM_OBJETO VARCHAR(20),
	ATAQUE FLOAT,
	AT_ESPECIAL FLOAT,
	DEFENSA FLOAT,
	DEF_ESPECIAL FLOAT,
	VELOCIDAD FLOAT,
	PRECIO INT
);

CREATE TABLE MOCHILA(
	ID_ENTRENADOR INT,
	ID_OBJETO INT,
	CANTIDAD INT NOT NULL,
	FOREIGN KEY (ID_ENTRENADOR) REFERENCES ENTRENADOR(ID_ENTRENADOR),
	FOREIGN KEY (ID_OBJETO) REFERENCES OBJETO(ID_OBJETO)
);
 
ALTER TABLE MOVIMIENTO_POKEMON
ADD CONSTRAINT MOV_POK_PK PRIMARY KEY (ID_POKEMON, ID_MOVIMIENTO);
 
ALTER TABLE MOVIMIENTO_POKEMON
ADD FOREIGN KEY (ID_POKEMON) REFERENCES POKEMON (ID_POKEMON);
 
ALTER TABLE MOVIMIENTO_POKEMON
ADD FOREIGN KEY (ID_MOVIMIENTO) REFERENCES MOVIMIENTO (ID_MOVIMIENTO);

ALTER TABLE POKEMON
ADD CONSTRAINT PKM_ID_RIV_FK FOREIGN KEY (ID_RIVAL) REFERENCES RIVAL (ID_RIVAL);

ALTER TABLE MOCHILA
ADD CONSTRAINT MOC_PK PRIMARY KEY (ID_ENTRENADOR, ID_OBJETO);

ALTER TABLE POKEMON
ADD CONSTRAINT PKM_ID_ENT_FK FOREIGN KEY (ID_ENTRENADOR) REFERENCES ENTRENADOR (ID_ENTRENADOR);

ALTER TABLE POKEMON
ADD CONSTRAINT PKM_NUM_PKX_FK FOREIGN KEY (NUM_POKEDEX) REFERENCES POKEDEX (NUM_POKEDEX);

ALTER TABLE POKEMON
ADD CONSTRAINT PKM_ID_RIVAL_FK FOREIGN KEY (ID_RIVAL) REFERENCES RIVAL (ID_RIVAL);

ALTER TABLE movimiento_pokemon
DROP FOREIGN KEY movimiento_pokemon_ibfk_1;
 
ALTER TABLE movimiento_pokemon
ADD CONSTRAINT movimiento_pokemon_ibfk_1
FOREIGN KEY (ID_POKEMON) REFERENCES pokemon(ID_POKEMON)
ON DELETE CASCADE;
 
ALTER TABLE POKEMON ADD ID_OBJETO INT REFERENCES OBJETO(ID_OBJETO);

ALTER TABLE OBJETO
ADD EQUIPABLE BOOLEAN DEFAULT FALSE;

UPDATE OBJETO
SET EQUIPABLE = TRUE
WHERE ID_OBJETO IN (1, 2, 3, 4, 5, 6, 7);


